const App = () => {
  // Header component
  const Header = (props) => {
    return (
      <div>
        <h1>
          {props.name}
        </h1>
      </div>
    )
  }

  const Part = (props) => {
    <p>
      {props.id} {props.ex}
    </p>
  }

  // Content component
  const Content = () => {
    return (
      <div>
        <Part id = {part1} ex = {exercises1}/> 
        <Part id = {part2} ex = {exercises2}/> 
        <Part id = {part3} ex = {exercises3}/> 
      </div>
    )
  }

  // Total component
  const Total = (props) => {
    return (
      <div>
        <p>
          Number of exercises {props.ex1 + props.ex2 + props.ex3}
        </p>
      </div>
    )
  }

  const course = 'Half Stack application development'
  const part1 = 'Fundamentals of React'
  const exercises1 = 10
  const part2 = 'Using props to pass data'
  const exercises2 = 7
  const part3 = 'State of a component'
  const exercises3 = 14

  return (
    <div>
      <Header name={course} />
      <Content id={part1} ex={exercises1}/>
      <Content id={part2} ex={exercises2}/>
      <Content id={part3} ex={exercises3}/>
      <Total ex1={exercises1} ex2 ={exercises2} ex3= {exercises3} />
    </div>
  )
}

export default App
