const App = () => {
  // Header component
  const Header = (props) => {
    return (
      <div>
        <h1>
          {props.name}
        </h1>
      </div>
    )
  }

  const Part = (props) => {
    <div>
      <p>
        {props.id} {props.ex}
      </p>
    </div>
  }

  // Content component
  const Content = (props) => {
    return (
      <div>
        <Part id = {props.id1} ex = {props.ex1}/> 
        <Part id = {props.id2} ex = {props.ex2}/> 
        <Part id = {props.id3} ex = {props.ex3}/> 
      </div>
    )
  }

  // Total component
  const Total = (props) => {
    return (
      <div>
        <p>
          Number of exercises {props.ex1 + props.ex2 + props.ex3}
        </p>
      </div>
    )
  }

  const course = 'Half Stack application development'
  const part1 = 'Fundamentals of React'
  const exercises1 = 10
  const part2 = 'Using props to pass data'
  const exercises2 = 7
  const part3 = 'State of a component'
  const exercises3 = 14

  return (
    <div>
      <Header name={course} />
      <Content id1 = {part1} id2 = {part2} id3 = {part3} ex1 = {exercises1} ex2 = {exercises2} ex3 = {exercises3}/>
      <Total ex1={exercises1} ex2 ={exercises2} ex3= {exercises3} />
    </div>
  )
}

export default App
