const Part = (props) => {
  return (
    <>
      <p>
        {props.id} {props.ex}
      </p>
    </>
  )
}


const Total = ({parts}) => {
  const initialValue = 0
  const total = parts.reduce((s,p) => s + p.exercises, 0)

  return (
    <>
      <b>
        Number of exercises {total}
      </b>
    </>
  )
}

const Content = ({parts}) => {
  return (
    <>

      <Part id = {parts[0].name} ex = {parts[0].exercises}/> 
      <Part id = {parts[1].name} ex = {parts[1].exercises}/> 
      <Part id = {parts[2].name} ex = {parts[2].exercises}/> 
    </>
  )
}

const Header = (props) => {
  return (
    <>
      <h2>
        {props.name}
      </h2>
    </>
  )
}

const Course = ({course}) => {
  return (
    <div>
      <Header name={course.name}/>
      <div>
       <Content parts={course.parts}/>
      </div>
      <div>
        <Total parts={course.parts}/>
      </div>
    </div>
  )
}

const Courses =( {courses} ) => {
  const c = courses.map(course => <Course course={course} />)
  return (
    <div>
      c
    </div>
  )
}

const App = () => {
  const courses = [{
    name: 'Half Stack application development',
    id: 1,
    parts: [
      {
        name: 'Fundamentals of React',
        exercises: 10,
        id: 1
      },
      {
        name: 'Using props to pass data',
        exercises: 7,
        id: 2
      },
      {
        name: 'State of a component',
        exercises: 14,
        id: 3
      },
      {
        name: 'Redux',
        exercises: 11,
        id: 4
      }
    ]
  },
  {
    name: 'Node.js',
    id: 2,
    parts: [
      {
        name: 'Routing',
        exercises: 3,
        id: 1
      },
      {
        name: 'Middlewares',
        exercises: 7,
        id: 2
      }
    ]
  } 
  ]

  return (
    <div>
      <h1>Web development curriculum</h1>
      <Courses courses={courses}/>
    </div>
  )
}

export default App
