const App = () => {
  // Header component
  const Header = (props) => {
    return (
      <>
        <h1>
          {props.name}
        </h1>
      </>
    )
  }

  const Part = (props) => {
    return (
      <>
        <p>
          {props.id} {props.ex}
        </p>
      </>
    )
  }

  // Content component
  const Content = (props) => {
    return (
      <>
        <Part id = {props.p1.name} ex = {props.p1.exercises}/> 
        <Part id = {props.p2.name} ex = {props.p2.exercises}/> 
        <Part id = {props.p3.name} ex = {props.p3.exercises}/> 
      </>
    )
  }

  // Total component
  const Total = (props) => {
    return (
      <>
        <p>
          Number of exercises {props.ex1 + props.ex2 + props.ex3}
        </p>
      </>
    )
  }

  const course = 'Half Stack application development'

  const part1 = {
    name: 'Fundamentals of React',
    exercises: 10
  }

  const part2 = {
    name: 'Using props to pass data',
    exercises: 7
  }

  const part3 = {
    name: 'State of a component',
    exercises: 14
  }

  return (
    <>
      <Header name={course} />
      <Content p1 = {part1} p2 = {part2} p3 = {part3}/>
      <Total ex1={exercises1} ex2 ={exercises2} ex3= {exercises3} />
    </>
  )
}

export default App
