const Part = (props) => {
  return (
    <>
      <p>
        {props.id} {props.ex}
      </p>
    </>
  )
}


const Total = ({parts}) => {
  const initialValue = 0
  const total = parts.reduce((s,p) => s + p.exercises, 0)

  return (
    <>
      <b>
        Number of exercises {total}
      </b>
    </>
  )
}

const Content = ({parts}) => {
  return (
    <div>
      {parts.map(p => <Part key={p.id} id = {p.id} ex = {p.exercises}/>)}
    </div>
  )
}

const Header = (props) => {
  return (
    <>
      <h2>
        {props.name}
      </h2>
    </>
  )
}

const Course = ({course}) => {
  return (
    <div>
      <Header name={course.name}/>
      <div>
       <Content parts={course.parts}/>
      </div>
      <div>
        <Total parts={course.parts}/>
      </div>
    </div>
  )
}


const App = () => {
  const courses = [{
    name: 'Half Stack application development',
    id: 1,
    parts: [
      {
        name: 'Fundamentals of React',
        exercises: 10,
        id: 1
      },
      {
        name: 'Using props to pass data',
        exercises: 7,
        id: 2
      },
      {
        name: 'State of a component',
        exercises: 14,
        id: 3
      },
      {
        name: 'Redux',
        exercises: 11,
        id: 4
      }
    ]
  },
  {
    name: 'Node.js',
    id: 2,
    parts: [
      {
        name: 'Routing',
        exercises: 3,
        id: 1
      },
      {
        name: 'Middlewares',
        exercises: 7,
        id: 2
      }
    ]
  } 
  ]

  return (
    <div>
      <h1>Web development curriculum</h1>
      <ul>
        {courses.map(course => <Course key={course.id} course={course} />)}
      </ul>
    </div>
  )
}

export default App
