const Part = (props) => {
  return (
    <>
      <p>
        {props.id} {props.ex}
      </p>
    </>
  )
}


const Total = ({parts}) => {
  const initialValue = 0
  const total = parts.reduce((s,p) => s.exercises + p.exercises, 0)


  //const total = parts.reduce( (s,p) => {
  //  console.log('what is happening', s.exercises,p.exercises)
  //  console.log('sum', s.exercises, p.exercises)
  //  return exercises: s.exercises+p.exercises
  //})

  return (
    <>
      <p>
        Number of exercises {total}
      </p>
    </>
  )
}

const Content = ({parts}) => {
  return (
    <>
      <Part id = {parts[0].name} ex = {parts[0].exercises}/> 
      <Part id = {parts[1].name} ex = {parts[1].exercises}/> 
      <Part id = {parts[2].name} ex = {parts[2].exercises}/> 
    </>
  )
}

const Header = (props) => {
  return (
    <>
      <h1>
        {props.name}
      </h1>
    </>
  )
}

const Course = ({course}) => {
  return (
    <div>
      <Header name={course.name}/>
      <div>
       <Content parts={course.parts}/>
      </div>
      <div>
        <Total parts={course.parts}/>
      </div>
    </div>
  )
}

const App = () => {
  const course = {
    name: 'Half Stack application development',
    id: 1,
    parts: [
      {
        name: 'Fundamentals of React',
        exercises: 10,
        id: 1
      },
      {
        name: 'Using props to pass data',
        exercises: 7,
        id: 2
      },
      {
        name: 'State of a component',
        exercises: 14,
        id: 3
      }
    ]
  }

  return (
    <div>
      <Course course={course}/>
    </div>
  )
}

export default App
